package leetcodemay;

import org.junit.Assert;
import org.junit.Test;

public class StringPermutationTest {

    @Test
    public void testIfS1IsPresentInS2() {
        String s2 = "eidbaooo";
        String s1 = "ab";
        Assert.assertTrue(new StringPermutation().checkInclusion(s1, s2));
    }

    @Test
    public void testIfS1IsNotPresentInS2() {
        String s1 = "ab";
        String s2 = "eidboaoo";
        Assert.assertFalse(new StringPermutation().checkInclusion(s1, s2));
    }

    @Test
    public void testIfkyisPresentInainwkckifykxlribaypk() {
        String s1 = "ky";
        String s2 = "ainwkckifykxlribaypk";
        Assert.assertTrue(new StringPermutation().checkInclusion(s1, s2));
    }

    @Test
    public void testifLargeStringS1IsPresentInLargeStringS2() {
        String s1 =
                "";
        String s2 =
                "";
        Assert.assertTrue(new StringPermutation().checkInclusion(s1,s2));
    }
}
